// Util functions for operations usually used with files.

'use strict';

/**
 * Split the given source string into its lines.  Respects Unix,
 * Windows und Macintosh line breaks.
 *
 * @param {string} src
 * @returns {string[]}
 */
function splitLines(src) {
  return src.split(/\r\n?|\n/);
}


/**
 * Change Windows style line endings to Unix style
 *
 * @param {string} src
 * @returns {string}
 */
function normalizeLineEndings(src) {
  return (src && process.platform === 'win32') ? src.replace(/\r\n/g, '\n') : src;
}


module.exports = {
  splitLines,
  normalizeLineEndings,
};
